import React, { useState, useEffect } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";
import "./App.css";

function App() {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState(game.fen());
  const [advice, setAdvice] = useState("");
  const [gameOver, setGameOver] = useState(false);
  const [vsComputer, setVsComputer] = useState(true); // Play vs Computer or Solo

  useEffect(() => {
    if (game.isGameOver()) {  // ðŸ›  Correct function
      setGameOver(true);
      return;
    }

    if (vsComputer && game.turn() === "b") {
      setTimeout(() => {
        makeComputerMove();
      }, 500);
    }
  }, [game, vsComputer]);

  const handleMove = (sourceSquare, targetSquare) => {
    if (!game || game.isGameOver()) return false; // ðŸ›  Correct function

    if (vsComputer && game.turn() !== "w") return false; // Player is White in vsComputer mode

    const move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // Always promote to queen
    });

    if (move === null) return false; // Illegal move

    setFen(game.fen());
    setGame(new Chess(game.fen()));
    setAdvice("");
    return true;
  };

  const makeComputerMove = () => {
    const moves = game.moves();
    if (moves.length === 0) {
      setGameOver(true);
      return;
    }
    const randomMove = moves[Math.floor(Math.random() * moves.length)];
    game.move(randomMove);
    setFen(game.fen());
    setGame(new Chess(game.fen()));
  };

  const getAdvice = () => {
    const possibleMoves = game.moves({ verbose: true });

    if (possibleMoves.length > 0) {
      const analyzedMoves = possibleMoves.map(move => {
        if (move.flags.includes('c')) return { move: move.san, reason: "captures a piece" };
        if (move.flags.includes('k')) return { move: move.san, reason: "puts the king in check" };
        if (move.flags.includes('e')) return { move: move.san, reason: "en passant capture" };
        if (['N', 'B', 'R', 'Q'].includes(move.piece.toUpperCase()) && (move.to[0] === 'd' || move.to[0] === 'e')) {
          return { move: move.san, reason: "controls the center" };
        }
        return { move: move.san, reason: "develops a piece" };
      });

      const randomAdvice = analyzedMoves[Math.floor(Math.random() * analyzedMoves.length)];
      setAdvice(`Hint: ${randomAdvice.move} because it ${randomAdvice.reason}.`);
    } else {
      setAdvice("No advice. Game might be over!");
    }
  };

  const resetGame = () => {
    const newGame = new Chess();
    setGame(newGame);
    setFen(newGame.fen());
    setAdvice("");
    setGameOver(false);
  };

  return (
    <div className="App">
      <h1>Chess Learning App</h1>

      {/* Play Mode Toggle */}
      <div style={{ marginBottom: 20 }}>
        <label>
          <input
            type="radio"
            checked={vsComputer}
            onChange={() => setVsComputer(true)}
          />
          Play vs Computer
        </label>
        <label style={{ marginLeft: 15 }}>
          <input
            type="radio"
            checked={!vsComputer}
            onChange={() => setVsComputer(false)}
          />
          Play Solo (2 players)
        </label>
      </div>

      <Chessboard
        position={fen}
        onPieceDrop={(source, target) => handleMove(source, target)}
        boardWidth={400}
      />

      <div style={{ marginTop: 20 }}>
        {gameOver ? (
          <h2>Game Over!</h2>
        ) : (
          <button onClick={getAdvice}>Get Smart Hint</button>
        )}
        {advice && <p>{advice}</p>}
        <br />
        <button onClick={resetGame}>Reset Game</button>
      </div>
    </div>
  );
}

export default App;
