import React, { useState, useEffect } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";
import Confetti from "react-confetti";
import "./App.css";

function App() {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState(game.fen());
  const [advice, setAdvice] = useState("");
  const [gameOver, setGameOver] = useState(false);
  const [vsComputer, setVsComputer] = useState(true);
  const [winner, setWinner] = useState("");

  useEffect(() => {
    if (game.isGameOver()) {
      setGameOver(true);
      setWinner(game.turn() === "w" ? "Black wins!" : "White wins!");
      return;
    }

    if (vsComputer && game.turn() === "b") {
      setTimeout(() => {
        makeComputerMove();
      }, 500);
    }
  }, [game, vsComputer]);

  const handleMove = (sourceSquare, targetSquare) => {
    if (!game || game.isGameOver()) return false;
    if (vsComputer && game.turn() !== "w") return false;

    const move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q",
    });

    if (move === null) return false;

    setFen(game.fen());
    setGame(new Chess(game.fen()));
    setAdvice("");
    return true;
  };

  const makeComputerMove = () => {
    const bestMove = findBestMove(game, 1);
    game.move(bestMove);
    setFen(game.fen());
    setGame(new Chess(game.fen()));
  };

  const findBestMove = (gameInstance, depth) => {
    const moves = gameInstance.moves();
    let bestEvaluation = -Infinity;
    let bestMove = moves[Math.floor(Math.random() * moves.length)];

    for (let move of moves) {
      const clone = new Chess(gameInstance.fen());
      clone.move(move);
      const evaluation = -evaluateBoard(clone);
      if (evaluation > bestEvaluation) {
        bestEvaluation = evaluation;
        bestMove = move;
      }
    }

    return bestMove;
  };

  const evaluateBoard = (board) => {
    const pieceValue = {
      p: 1,
      n: 3,
      b: 3,
      r: 5,
      q: 9,
      k: 100,
    };
    let evaluation = 0;
    const boardState = board.board();
    for (let row of boardState) {
      for (let piece of row) {
        if (piece) {
          const value = pieceValue[piece.type] || 0;
          evaluation += piece.color === "w" ? value : -value;
        }
      }
    }
    return evaluation;
  };

  const getAdvice = () => {
    const possibleMoves = game.moves({ verbose: true });

    if (possibleMoves.length > 0) {
      const explainedMoves = possibleMoves.map((move) => {
        if (move.flags.includes('c')) return { move: move.san, reason: "captures an opponent's piece" };
        if (move.flags.includes('k')) return { move: move.san, reason: "puts the king in check" };
        if (['d', 'e'].includes(move.to[0])) return { move: move.san, reason: "controls the center" };
        if (move.flags.includes('e')) return { move: move.san, reason: "special en passant capture" };
        return { move: move.san, reason: "develops a piece toward safety or activity" };
      });

      const randomAdvice = explainedMoves[Math.floor(Math.random() * explainedMoves.length)];
      setAdvice(`Hint: Play ${randomAdvice.move} because it ${randomAdvice.reason}.`);
    } else {
      setAdvice("No advice available.");
    }
  };

  const resetGame = () => {
    const newGame = new Chess();
    setGame(newGame);
    setFen(newGame.fen());
    setAdvice("");
    setGameOver(false);
    setWinner("");
  };

  return (
    <div className="App">
      <h1>Chess Learning App</h1>

      {gameOver && <Confetti />}
      {winner && <h2>{winner}</h2>}

      {/* Play Mode Toggle */}
      <div style={{ marginBottom: 20 }}>
        <label>
          <input
            type="radio"
            checked={vsComputer}
            onChange={() => setVsComputer(true)}
          />
          Play vs Computer
        </label>
        <label style={{ marginLeft: 15 }}>
          <input
            type="radio"
            checked={!vsComputer}
            onChange={() => setVsComputer(false)}
          />
          Play Solo (2 players)
        </label>
      </div>

      {/* üìè Centered Big Chessboard */}
      <div style={{ display: "flex", justifyContent: "center", marginTop: 20 }}>
        <Chessboard
          position={fen}
          onPieceDrop={(source, target) => handleMove(source, target)}
          boardWidth={600}  // Bigger board
        />
      </div>

      <div style={{ marginTop: 20, textAlign: "center" }}>
        {gameOver ? (
          <button onClick={resetGame}>New Game</button>
        ) : (
          <>
            <button onClick={getAdvice}>Get Smart Hint</button>
            {advice && <p>{advice}</p>}
            <br />
            <button onClick={resetGame}>Reset Game</button>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
