import React, { useState, useEffect } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";
import "./App.css";

function App() {
  const [game, setGame] = useState(new Chess());
  const [moveHistory, setMoveHistory] = useState([]);
  const [advice, setAdvice] = useState("Make your first move!");
  const [hint, setHint] = useState("");

  function safeGameMutate(modify) {
    setGame((g) => {
      const updated = new Chess(g.fen());
      modify(updated);
      return updated;
    });
  }

  function evaluateMove(prevGame, move) {
    const prevEval = evaluatePosition(prevGame);
    const newEval = evaluatePosition(game);
    const evalDiff = newEval - prevEval;

    if (evalDiff < -0.5) {
      return "‚ö†Ô∏è That move made your position worse. Look out for threats!";
    } else if (evalDiff > 0.5) {
      return "‚úÖ Great move! You improved your position.";
    } else {
      return "‚ûñ Solid move, but there might be stronger ones.";
    }
  }

  function evaluatePosition(chessInstance) {
    const material = {
      p: 1,
      n: 3,
      b: 3,
      r: 5,
      q: 9,
    };

    let score = 0;
    const board = chessInstance.board();
    for (let row of board) {
      for (let piece of row) {
        if (piece) {
          const value = material[piece.type] || 0;
          score += piece.color === "w" ? value : -value;
        }
      }
    }
    return score;
  }

  function onDrop(sourceSquare, targetSquare, piece) {
    let move = null;
    safeGameMutate((gameInstance) => {
      move = gameInstance.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: "q", // always promote to queen by default
      });
    });

    if (move === null) return false;

    setMoveHistory((h) => [...h, move]);
    const adviceMsg = evaluateMove(game, move);
    setAdvice(adviceMsg);
    setHint("");

    // Computer makes move
    setTimeout(() => {
      makeComputerMove();
    }, 500);

    return true;
  }

  function makeComputerMove() {
    safeGameMutate((gameInstance) => {
      const moves = gameInstance.moves();
      if (moves.length > 0) {
        const move = moves[Math.floor(Math.random() * moves.length)];
        gameInstance.move(move);
      }
    });
  }

  function getHint() {
    const possibleMoves = game.moves({ verbose: true });
    if (possibleMoves.length === 0) {
      setHint("No hints available - checkmate or stalemate!");
      return;
    }

    let bestMove = null;
    let bestEval = -Infinity;
    possibleMoves.forEach((move) => {
      const tempGame = new Chess(game.fen());
      tempGame.move(move.san);
      const evaluation = evaluatePosition(tempGame);
      if (evaluation > bestEval) {
        bestEval = evaluation;
        bestMove = move;
      }
    });

    if (bestMove) {
      setHint(
        `üí° Hint: Consider moving ${bestMove.from} to ${bestMove.to}. It improves your position!`
      );
    } else {
      setHint("Couldn't find a strong hint. Trust your instincts!");
    }
  }

  return (
    <div className="App" style={{ padding: 20 }}>
      <h1>Learn Chess App ‚ôüÔ∏è</h1>
      <Chessboard
        position={game.fen()}
        onPieceDrop={onDrop}
        boardWidth={500}
        customBoardStyle={{
          borderRadius: "10px",
          boxShadow: "0 5px 15px rgba(0,0,0,0.5)",
        }}
      />
      <div
        style={{
          marginTop: 20,
          padding: 15,
          backgroundColor: "#f0f0f0",
          borderRadius: "10px",
          minHeight: "100px",
          textAlign: "left",
        }}
      >
        <h3>Advice:</h3>
        <p>{advice}</p>
        {hint && (
          <>
            <h4>Hint:</h4>
            <p>{hint}</p>
          </>
        )}
      </div>
      <button
        onClick={getHint}
        style={{
          marginTop: 20,
          padding: "10px 20px",
          fontSize: "16px",
          borderRadius: "8px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          cursor: "pointer",
        }}
      >
        Get a Hint
      </button>
    </div>
  );
}

export default App;
