// src/App.js
import React, { useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

function App() {
  const [game, setGame] = useState(new Chess());
  const [message, setMessage] = useState("Welcome! Make your first move.");
  const [pendingPromotion, setPendingPromotion] = useState(null);

  function makeAMove(move) {
    const gameCopy = new Chess(game.fen());
    const result = gameCopy.move(move);
    if (result) {
      setGame(gameCopy);
      giveAdvice(gameCopy);
    }
    return result;
  }

  function giveAdvice(currentGame) {
    if (currentGame.isCheckmate()) {
      setMessage("Checkmate! Great job!");
    } else if (currentGame.isCheck()) {
      setMessage("Careful! Your king is in check.");
    } else {
      setMessage(getRandomAdvice());
    }
  }

  function getRandomAdvice() {
    const adviceList = [
      "Control the center!",
      "Develop your pieces early.",
      "Protect your king.",
      "Don't move the same piece twice in the opening.",
      "Think before every move: checks, captures, threats.",
    ];
    return adviceList[Math.floor(Math.random() * adviceList.length)];
  }

  function onDrop(sourceSquare, targetSquare) {
    const moves = game.moves({ verbose: true });
    const move = moves.find(
      (m) => m.from === sourceSquare && m.to === targetSquare
    );

    if (move && move.flags.includes("p")) {
      // pawn promotion needed
      setPendingPromotion({ from: sourceSquare, to: targetSquare });
      return false;
    }

    const moveResult = makeAMove({ from: sourceSquare, to: targetSquare });
    if (moveResult === null) return false;
    return true;
  }

  function promote(promotionPiece) {
    if (!pendingPromotion) return;

    const { from, to } = pendingPromotion;
    const move = { from, to, promotion: promotionPiece };

    makeAMove(move);
    setPendingPromotion(null);
  }

  function renderPromotionDialog() {
    if (!pendingPromotion) return null;

    return (
      <div style={styles.promotionDialog}>
        <p>Choose promotion:</p>
        <div style={styles.promotionChoices}>
          {["q", "r", "b", "n"].map((p) => (
            <button key={p} style={styles.promotionButton} onClick={() => promote(p)}>
              {p.toUpperCase()}
            </button>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="App" style={{ padding: "20px", textAlign: "center" }}>
      <h1>Learn Chess App</h1>
      <p>{message}</p>
      <div style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}>
        <Chessboard position={game.fen()} onPieceDrop={onDrop} boardWidth={400} />
      </div>
      {renderPromotionDialog()}
    </div>
  );
}

const styles = {
  promotionDialog: {
    position: "absolute",
    top: "30%",
    left: "50%",
    transform: "translate(-50%, -30%)",
    backgroundColor: "white",
    padding: "20px",
    border: "2px solid black",
    borderRadius: "10px",
    zIndex: 5,
  },
  promotionChoices: {
    display: "flex",
    justifyContent: "space-around",
    marginTop: "10px",
  },
  promotionButton: {
    fontSize: "24px",
    padding: "10px",
    margin: "5px",
    cursor: "pointer",
  },
};

export default App;
