import React, { useState, useEffect } from 'react';
import { Chess } from 'chess.js';  // Ensure the correct import
import { Chessboard } from 'react-chessboard';
import Confetti from 'react-confetti';

function App() {
  const [game, setGame] = useState(new Chess());  // Initialize game properly
  const [fen, setFen] = useState(game.fen());
  const [isGameOver, setIsGameOver] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [advice, setAdvice] = useState('');

  // Check if the game is over
  useEffect(() => {
    if (game.game_over()) {  // This should work now as game is a Chess instance
      setIsGameOver(true);
      setShowConfetti(true);
    } else {
      setIsGameOver(false);
    }
  }, [game]);

  // Function to get advice
  const getAdvice = () => {
    if (game.game_over()) {
      setAdvice('Game Over! Checkmate or Stalemate.');
      return;
    }
    // Example of simple advice
    const moves = game.ugly_moves();
    const bestMove = moves[Math.floor(Math.random() * moves.length)];
    setAdvice(`Consider moving ${bestMove.san} to improve your position.`);
  };

  // Handle move
  const handleMove = (from, to) => {
    const gameCopy = { ...game }; // Copy the game state
    const move = gameCopy.ugly_move({ from, to });
    if (move === null) return; // If the move is illegal, do nothing

    setGame(gameCopy);
    setFen(gameCopy.fen());
    getAdvice(); // Get advice after each move
  };

  // Function to handle reset
  const handleReset = () => {
    const newGame = new Chess();
    setGame(newGame);
    setFen(newGame.fen());
    setIsGameOver(false);
    setShowConfetti(false);
    setAdvice('');
  };

  return (
    <div className="App">
      <h1>Chess Game</h1>
      <Chessboard
        position={fen}
        onPieceDrop={(from, to) => handleMove(from, to)}
        draggable={true}
        width={400}
      />
      {isGameOver && showConfetti && (
        <Confetti width={window.innerWidth} height={window.innerHeight} />
      )}
      <div>
        <h2>Game Advice</h2>
        <p>{advice}</p>
      </div>
      <button onClick={handleReset}>Reset Game</button>
    </div>
  );
}

export default App;
