import React, { useState, useEffect } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';  // Import chess.js

const App = () => {
  const [game, setGame] = useState(new Chess());
  const [boardPosition, setBoardPosition] = useState(game.fen());
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    // Log the game object to inspect its structure
    console.log("Game object: ", game); // Inspect the structure of the game object

    // Check if game_over() method exists on the game object
    if (game && typeof game.game_over === "function") {
      console.log("game_over exists as a function.");
      if (game.game_over()) {
        setGameOver(true);
        alert("Game Over!");
      }
    } else {
      console.error("game_over method not found on the game object.");
    }
  }, [game]);

  const handleMove = (move) => {
    let gameCopy = { ...game };  // Make a copy of the game
    const result = gameCopy.ugly_move(move);
    if (result) {
      setGame(gameCopy);  // Update game state
      setBoardPosition(gameCopy.fen());  // Update board position
    }
  };

  const getAdvice = () => {
    // Function to provide move advice or analyze position
    const moves = game.legal_moves();
    if (moves.length > 0) {
      return moves[Math.floor(Math.random() * moves.length)];  // Get a random legal move as advice
    }
    return null;
  };

  const handleDrop = (source, target) => {
    const move = { from: source, to: target };
    handleMove(move);
  };

  return (
    <div className="App">
      <h1>Chess Learning App</h1>
      <div>
        <Chessboard
          position={boardPosition}
          onPieceDrop={(source, target) => handleDrop(source, target)}  // Handle piece drop
        />
      </div>
      {gameOver && <div className="game-over">Game Over!</div>}
      {!gameOver && (
        <div>
          <button onClick={() => alert(`Advice: Try the move ${getAdvice()?.to}`)}>
            Get Advice
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
