import React, { useState } from 'react';
import { DragDropContext } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Chessboard from 'chessboardjsx';  // Assuming you're using chessboardjsx for your board display
import { Chess } from 'chess.js';  // Chess logic (assuming you're using chess.js for game logic)

function App() {
  // Initialize chess game logic using chess.js
  const [game, setGame] = useState(new Chess());

  // Handle piece drag start (optional for game logic)
  const handleDragStart = (monitor) => {
    const item = monitor.getItem();
    console.log("Drag started: ", item);
  };

  // Handle drop on chessboard
  const handleDrop = (item, monitor) => {
    const { fromSquare, toSquare } = item;

    const move = game.move({
      from: fromSquare,
      to: toSquare,
      promotion: 'q', // Automatically promote pawns to queens
    });

    if (move === null) {
      return;
    }

    setGame(game);  // Update the game state with the new move
    console.log("Moved:", move);
  };

  // Render the chessboard and game logic
  return (
    <DragDropContext backend={HTML5Backend} onDrop={handleDrop} onDragStart={handleDragStart}>
      <div className="App">
        <h1>Chess Learning App</h1>

        {/* Render chessboard with drag and drop support */}
        <Chessboard 
          position={game.fen()} // Pass current board position in Forsyth-Edwards Notation (FEN)
          draggable={true}  // Enable piece dragging
          onDrop={handleDrop}  // Call the handleDrop when a piece is dropped
        />

        {/* You can add additional controls for reset, undo, etc. */}
        <button onClick={() => setGame(new Chess())}>Start New Game</button>
      </div>
    </DragDropContext>
  );
}

export default App;
