import React, { useState, useEffect } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js'; // Import chess.js

const App = () => {
  const [game, setGame] = useState(new Chess());
  const [boardPosition, setBoardPosition] = useState(game.fen());
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    // Log the game object to inspect its structure
    console.log("Game object: ", game); // Inspect the structure of the game object

    // Check if game_over() method exists on the game object
    if (game.game_over()) {
      setGameOver(true);
      alert("Game Over!");
    }

    // If the game isn't over, the computer can play
    if (!gameOver && game.turn() === 'b') {
      makeComputerMove();
    }
  }, [game, gameOver]);

  // Function to make a move for the computer
  const makeComputerMove = () => {
    const legalMoves = game.legal_moves();
    if (legalMoves.length === 0) return; // No legal moves, game over
    const randomMove = legalMoves[Math.floor(Math.random() * legalMoves.length)];
    game.ugly_move(randomMove);
    setGame(new Chess(game.fen())); // Update game state after the computer's move
  };

  // Handle the player's move
  const handleMove = (move) => {
    let gameCopy = { ...game };
    const result = gameCopy.ugly_move(move);
    if (result) {
      setGame(gameCopy); // Update game state
      setBoardPosition(gameCopy.fen()); // Update board position

      // If the game isn't over and it's the computer's turn, let the computer play
      if (!gameCopy.game_over() && gameCopy.turn() === 'b') {
        makeComputerMove();
      }
    }
  };

  // Function to provide advice (next legal move for the player)
  const getAdvice = () => {
    const moves = game.legal_moves();
    if (moves.length > 0) {
      const randomMove = moves[Math.floor(Math.random() * moves.length)];
      return `Try the move ${randomMove.san}`; // Return the standard algebraic notation for the move
    }
    return 'No moves available!';
  };

  // Handle the drop event when the player drops a piece
  const handleDrop = (source, target) => {
    const move = { from: source, to: target, promotion: 'q' }; // Default promotion to queen
    handleMove(move);
  };

  return (
    <div className="App">
      <h1>Chess Learning App</h1>
      <div>
        <Chessboard
          position={boardPosition}
          onPieceDrop={(source, target) => handleDrop(source, target)} // Handle piece drop
        />
      </div>
      {gameOver && <div className="game-over">Game Over!</div>}
      {!gameOver && (
        <div>
          <button onClick={() => alert(getAdvice())}>Get Advice</button>
        </div>
      )}
    </div>
  );
};

export default App;
