import React, { useState } from 'react';
import { Chess } from 'chess.js';
import { Chessboard } from 'react-chessboard';

function App() {
  const [game, setGame] = useState(new Chess());
  const [advice, setAdvice] = useState('Make your move!');

  function handleMove(sourceSquare, targetSquare) {
    const gameCopy = new Chess(game.fen());

    const move = gameCopy.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: 'q', // always promote to queen for simplicity
    });

    if (move === null) return false; // illegal move

    setGame(gameCopy);

    if (gameCopy.isCheckmate()) {
      setAdvice('Checkmate! You lost.');
    } else if (gameCopy.isCheck()) {
      setAdvice('You are in check! Be careful.');
    } else if (gameCopy.isDraw()) {
      setAdvice('It\'s a draw.');
    } else {
      setAdvice('Good move! Think about your next one.');
    }

    return true;
  }

  function restartGame() {
    setGame(new Chess());
    setAdvice('Game restarted. Make your move!');
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>Learn Chess App</h1>
      <button onClick={restartGame} style={{ marginBottom: 10 }}>Restart Game</button>
      <div style={{ display: 'flex', flexDirection: 'row' }}>
        <Chessboard 
          position={game.fen()} 
          onPieceDrop={handleMove}
          boardWidth={400}
        />
        <div style={{ marginLeft: 20 }}>
          <h2>Advice:</h2>
          <p>{advice}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
