import React, { useState } from 'react';
import { Chess } from 'chess.js';  // Ensure this import is correct
import { Chessboard } from 'react-chessboard';
import Confetti from 'react-confetti';

function App() {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState(game.fen());
  const [advice, setAdvice] = useState('');

  // Function to get advice
  const getAdvice = () => {
    // Example of simple advice
    const moves = game.ugly_moves();
    const bestMove = moves[Math.floor(Math.random() * moves.length)];
    setAdvice(`Consider moving ${bestMove.san} to improve your position.`);
  };

  // Handle move
  const handleMove = (from, to) => {
    const gameCopy = { ...game }; // Copy the game state
    const move = gameCopy.ugly_move({ from, to });
    if (move === null) return; // If the move is illegal, do nothing

    setGame(gameCopy);
    setFen(gameCopy.fen());
    getAdvice(); // Get advice after each move
  };

  // Function to handle reset
  const handleReset = () => {
    const newGame = new Chess();
    setGame(newGame);
    setFen(newGame.fen());
    setAdvice('');
  };

  return (
    <div className="App">
      <h1>Chess Game</h1>
      <Chessboard
        position={fen}
        onPieceDrop={(from, to) => handleMove(from, to)}
        draggable={true}
        width={400}
      />
      <div>
        <h2>Game Advice</h2>
        <p>{advice}</p>
      </div>
      <button onClick={handleReset}>Reset Game</button>
    </div>
  );
}

export default App;
