// src/App.js

import React, { useState } from "react";
import Chessboard from "react-chessboard";
import { Chess } from "chess.js";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { motion } from "framer-motion";

const App = () => {
  const [game, setGame] = useState(new Chess());
  const [adviceText, setAdviceText] = useState("");
  const [adviceColor, setAdviceColor] = useState("black");

  const handleMove = (sourceSquare, targetSquare) => {
    const gameCopy = new Chess(game.fen());
    const move = gameCopy.move({ from: sourceSquare, to: targetSquare, promotion: "q" });

    if (move === null) return false;

    setGame(gameCopy);

    if (gameCopy.isCheckmate()) {
      setAdviceText("Checkmate! Great job!");
      setAdviceColor("green");
    } else if (gameCopy.isCheck()) {
      setAdviceText("Careful! You're in check.");
      setAdviceColor("red");
    } else {
      giveAdvice(move);
    }

    return true;
  };

  const giveAdvice = (move) => {
    if (move.flags.includes("c")) {
      setAdviceText("Nice capture!");
      setAdviceColor("green");
    } else if (move.san.includes("+")) {
      setAdviceText("You put your opponent in check!");
      setAdviceColor("blue");
    } else {
      setAdviceText("Solid move. Think about controlling the center!");
      setAdviceColor("black");
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center", padding: 20 }}>
      <h1>Learn Chess App</h1>
      <DndProvider backend={HTML5Backend}>
        <Chessboard position={game.fen()} onPieceDrop={handleMove} boardWidth={400} />
      </DndProvider>

      {adviceText && (
        <motion.div
          key={adviceText}
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          style={{
            backgroundColor: "#f9f9f9",
            border: `2px solid ${adviceColor}`,
            padding: "10px 20px",
            marginTop: "20px",
            borderRadius: "10px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
            fontSize: "18px",
            fontWeight: "bold",
            color: adviceColor,
            maxWidth: "400px",
            textAlign: "center"
          }}
        >
          {adviceText}
        </motion.div>
      )}
    </div>
  );
};

export default App;
