import React, { useState } from 'react';
import { Chess } from 'chess.js';
import { Chessboard } from 'react-chessboard';

function App() {
  const [game, setGame] = useState(new Chess());
  const [gameOver, setGameOver] = useState(false);
  const [advice, setAdvice] = useState('');
  const [moveHistory, setMoveHistory] = useState([]);

  const getGameCopy = () => {
    return new Chess(game.fen());
  };

  const handleMove = (sourceSquare, targetSquare) => {
    if (gameOver) return false;

    const gameCopy = getGameCopy();
    const move = gameCopy.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: 'q', // always promote to queen
    });

    if (move === null) return false;

    setGame(gameCopy);
    const updatedHistory = [...moveHistory, move];
    setMoveHistory(updatedHistory);

    if (gameCopy.in_checkmate()) {
      setGameOver(true);
      setAdvice('Checkmate! Good game! Remember to always watch for your opponent’s threats.');
    } else if (gameCopy.in_draw()) {
      setGameOver(true);
      setAdvice('Draw! Stay aggressive and look for winning chances.');
    } else {
      setAdvice(generateAdvice(updatedHistory, gameCopy));
    }

    return true;
  };

  const generateAdvice = (history, currentGame) => {
    // Check if they are moving only pawns early on
    const first10Moves = history.slice(0, 10);
    const pawnMoves = first10Moves.filter(move => move.piece === 'p').length;
    if (pawnMoves >= 6) {
      return "You're moving too many pawns early. Try developing your knights and bishops.";
    }

    // Check for repeating piece moves early
    const pieceMoveCounts = {};
    for (let move of first10Moves) {
      const pieceKey = move.piece + move.color;
      pieceMoveCounts[pieceKey] = (pieceMoveCounts[pieceKey] || 0) + 1;
    }
    const repeatMoves = Object.values(pieceMoveCounts).some(count => count > 3);
    if (repeatMoves) {
      return "Don't move the same piece over and over early. Develop ALL your pieces.";
    }

    // Check if castling is missed after many moves
    if (!currentGame.castlingRights.includes('K') && !currentGame.castlingRights.includes('Q')) {
      if (!currentGame.history().some(move => move.san === 'O-O' || move.san === 'O-O-O')) {
        return "Remember to castle early to keep your king safe!";
      }
    }

    // Otherwise general advice
    return "Nice move! Keep developing pieces toward the center.";
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Learn Chess</h1>
      <Chessboard position={game.fen()} onPieceDrop={handleMove} />
      {gameOver && <h2 style={{ color: 'red' }}>Game Over</h2>}
      {advice && <p style={{ marginTop: '10px', fontStyle: 'italic' }}>{advice}</p>}
    </div>
  );
}

export default App;
