import React, { useState } from "react";
import { Chess } from "chess.js"; // Correct import
import { Chessboard } from "react-chessboard"; // Chessboard UI
import "./App.css"; // Optional if you have styles

function App() {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState(game.fen());
  const [advice, setAdvice] = useState("");
  const [gameOver, setGameOver] = useState(false);

  // Function to handle player move
  const handleMove = (sourceSquare, targetSquare) => {
    const newGame = new Chess(game.fen()); // Make a new game from current FEN
    const move = newGame.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // Always promote to queen
    });

    if (move === null) return false; // Illegal move

    setGame(newGame);
    setFen(newGame.fen());
    setAdvice("");
    if (newGame.isGameOver()) {
      setGameOver(true);
    } else {
      setTimeout(makeComputerMove, 500); // Let computer move after 0.5 sec
    }
    return true;
  };

  // Function for computer move
  const makeComputerMove = () => {
    const newGame = new Chess(game.fen());
    const possibleMoves = newGame.moves();
    if (possibleMoves.length === 0) return;

    const randomMove = possibleMoves[Math.floor(Math.random() * possibleMoves.length)];
    newGame.move(randomMove);
    setGame(newGame);
    setFen(newGame.fen());

    if (newGame.isGameOver()) {
      setGameOver(true);
    }
  };

  // Function to get advice
  const getAdvice = () => {
    const possibleMoves = game.moves();
    if (possibleMoves.length > 0) {
      const randomMove = possibleMoves[Math.floor(Math.random() * possibleMoves.length)];
      setAdvice(`Hint: Try the move ${randomMove}`);
    } else {
      setAdvice("No advice. Game might be over!");
    }
  };

  // Reset game
  const resetGame = () => {
    const newGame = new Chess();
    setGame(newGame);
    setFen(newGame.fen());
    setAdvice("");
    setGameOver(false);
  };

  return (
    <div className="App">
      <h1>Chess Learning App</h1>
      <Chessboard
        position={fen}
        onPieceDrop={(source, target) => handleMove(source, target)}
        boardWidth={400}
      />
      <div style={{ marginTop: 20 }}>
        {gameOver ? <h2>Game Over!</h2> : <button onClick={getAdvice}>Get Hint</button>}
        {advice && <p>{advice}</p>}
        <br />
        <button onClick={resetGame}>Reset Game</button>
      </div>
    </div>
  );
}

export default App;
